mapper.select:
select count(userid)
from userinfo
where userid = #{userid}

select userid, username
from userinfo
where userid = #{userId} and userpwd = #{userPwd}

select userid, userpwd, username, useraddr, usertel, joindate
from userinfo
where userid = #{userid}

mapper.update:
update userinfo
userpwd = #{userPwd}, username = #{userName}, useraddr = #{userAddr}, usertel = #{userTel},
where userid = #{userId}
